/* Private function prototypes declarations   -------------------------------------------*/
static void GLCD_voTask(void *pvoArgument);

/* Private functions definition   -------------------------------------------------------*/
static void GLCD_voTask(void *pvoArgument)
{
    GLCD_enInit();
    GLCD_voDisplayString(0, 0, "Thong");
    osDelay(2000);
    GLCD_voClearScreen();
    GLCD_voSetGraphicMode(eENABLE);
    GLCD_voDrawBitMap(acu8BitMap);
    osDelay(2000);
    GLCD_voClearScreen();
    GLCD_voDrawBitMap(acu8Logo);

    for (;;)
    {
        GLCD_Delay_us(100);
    }
}

/* Export functions definition   --------------------------------------------------------*/
void GLCD_voTaskTestInit(void)
{
    GLCD_pvoTaskHandle = osThreadNew(GLCD_voTask, NULL, &stGlcdTask);
}
